mapper.select:
select w.APPLY_NO AS applyNo, c.ORGAN_NO AS organNo, c.DERP_NO AS deptNo, o1.ORGAN_NAME AS companyname, o2.ORGAN_NAME AS deptname, c.USER_NO AS userNo, c.CUST_NAME AS custName, w.WORKOT_TYPE AS WORKOT_TYPE, w.WORKOT_START_TIME AS WORKOT_START_TIME, w.WORKOT_END_TIME AS WORKOT_END_TIME, w.TIME_SHEET AS TIME_SHEET, w.REMARK AS remark, w.ATTACHM AS attachm, w.CREATED_BY AS createdBy, w.CREATED_DATE AS createdDate from (select * from EP_CUST_INFO where ORGAN_NO = #{organNo}) c inner join IFDP_SYS_ORGAN_BASE o1 on c.ORGAN_NO = o1.ORGAN_NO inner join IFDP_SYS_ORGAN_BASE o2 on c.DERP_NO = o2.ORGAN_NO inner join EP_WORKOT_FLOW w on c.USER_NO=w.USER_NO where w.UID=#{uid}
and c.USER_NO =#{userNo}
and w.WORKOT_START_TIME > #{workotStartTime}
and w.WORKOT_END_TIME < #{workotEndTime}
order by w.APPLY_NO desc
