mapper.select:
select m.sys_no as sysNo, f.bsn_no as bsnNo, f.method_name as methodName, f.interface as interfaceName, f.bsn_url as bsnUrl, f.status as status, f.remark as remark, f.created_by as createdBy, f.created_date as createdDate, f.updated_date as updatedDate, f.updated_by as updatedBy from IFDP_SYS_BSN f left outer join IFDP_SYS_MENU_FUNC fm on f.bsn_no = fm.bsn_no left outer join IFDP_SYS_MENU m on fm.sys_no = m.sys_no and fm.menu_no = m.menu_no where f.uid = #{uid}
and m.sys_no =#{sysNo}
and f.bsn_no = #{bsnNo}
and f.method_name like '%${methodName}%'
and f.status =#{status}
